import React, { Component } from 'react'

 class Messagewithoutbinding extends React.Component {
    state;
   constructor(props){
    super(props)
    this.state={
        msg:'hi'
    }
     updateHandler=(value)=>{
        this.setState({msg:value})
    }
   }

  render() {
    return <div>
        <h1>{this.msg}</h1>
        <button onClick={this.updateHandler.bind(this,"Good Morning")}></button>
    </div>
  }
}

export default Messagewithoutbinding
