import React from 'react'
class Message3 extends React.Component{
    state={
        msg:'Hello'
    }
    gmHandler=()=>{
     this.setState({msg:'Good Morning'})
    }
 render(){

    return <div>
        <h1>{this.state.msg}</h1>
        <button onClick={this.gmHandler} >GM</button>
        <button>GA</button>
        <button>GE</button>
        <button>GN</button>
    </div>
 }
}
export default Message3
