import React, { Component } from 'react'

export class Messagewithoutbinding extends React.Component {
  state={
    msg:"Hello"
  }
  Handler=(value)=>{
    this.setState({msg:value})
  }
  render(){
    return <div>
     <h1>{this.state.msg}</h1>
     <button onClick={this.Handler.bind(this,"Good morning")}>GM</button>
     <button onclick={this.Handler.bind(this,"GoodNight")}>GN</button>
     
    </div>
  }
}

export default Messagewithoutbinding
