export class Messagewithoutbinding extends React.Component {
  state={
    msg:"Hllo"
  }
  gmHandler=()=>{
    this.setState({msg:"Good morning"})
  }
  render() {
    return <div>
   <h1>Value:{this.state.msg}</h1>
   <button onClick={(gm)}>GM</button>
    </div>
  }
}

export default Messagewithoutbinding
