   state={
    qty:1
   } 
   incrHandler=()=>{
      this.setState({qty:this.state.qty+1})
   }
   decrHandler=()=>{
    this.setState({qty:this.state.qty-1})
   }
render(){
    clg
        return <div>
            <button onClick={this.decrHandler}>-</button>
            {this.state.qty}
            <button onClick={this.incrHandler}>+</button>
        </div>
    }
}
export default Quatity
