import { useState } from "react"

let Message5=()=>{
    let[msg,setMsg]=useState('hello')
   let updateHandler=(value)=>{
      setMsg("hi ")
    }
 return <div>
    <h1>Value:{msg}</h1>
    <button onClick={updateHandler.i('Good Morning')}>GM</button>
    <button onClick={updateHandler.bind('Good Afternoon')}>GA</button>
    <button onClick={updateHandler.bind('Good Evening')}>GE</button>
    <button onClick={updateHandler.bind('Good Night')}>GN</button>
 </div>
}
export default Message5
