import React, { Component } from 'react'

export class Messagewithoutbinding extends React.Component {
  state={
    msg:"Hello"
  }
  
  render(){
    return <div>
     <h1>{this.state.msg}</h1>
     <button onClick={()=>{this.setState({msg:"Good Morning"})}}>GM</button>
     <button onClick={()=>{this.setState({msg:"Good Night"})}}>GM</button>
     
    </div>
  }
}

export default Messagewithoutbinding
