import React from "react";
class Messagewithoutbinding extends React.Component{
  state = {
    msg:"Apple"
  }
  handler = (value)=>{
    this.setState({msg:value})
  }
  render(){
    return <div>
      <h2>Message without binding</h2>
      <p><b>value:</b>{this.state.msg}</p>
      <button onClick={this.handler.bind(this,"Banana")}>button</button>
      <button onClick={this.handler.bind()}>click</button>

    </div>
  }
}
export default Messagewithoutbinding
